drop table if exists pm;
drop table if exists messages;
drop table if exists likes;
drop table if exists sales;
drop table if exists friendships;
drop table if exists codes;
drop table if exists users;

CREATE TABLE users (
    id SERIAL primary key,
    username TEXT NOT NULL UNIQUE CHECK (username <> ''), 
    yourname TEXT NOT NULL CHECK(yourname <> ''), 
    email TEXT NOT NULL UNIQUE CHECK (email <> ''),
    hashkeys TEXT NOT NULL CHECK (hashkeys <> ''),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    bio TEXT,
    pic TEXT
);


CREATE TABLE codes (
    id SERIAL primary key,
    email TEXT NOT NULL UNIQUE CHECK (email <> ''),
    code TEXT NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE friendships( 
    id SERIAL PRIMARY KEY, 
    sender_id INT REFERENCES users(id) NOT NULL, 
    recipient_id INT REFERENCES users(id) NOT NULL, 
    accepted BOOLEAN DEFAULT false );


CREATE TABLE sales(
    id SERIAL PRIMARY KEY, 
    seller_id INT REFERENCES users(id) NOT NULL, 

    item_name TEXT NOT NULL, 
    item_des TEXT,
    item_pic TEXT NOT NULL,
   
    item_price INT NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);


CREATE TABLE likes(
    id SERIAL PRIMARY KEY, 
    liker_id INT REFERENCES users(id) NOT NULL, 

    item_id INT REFERENCES sales(id) NOT NULL, 
     
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
    );

CREATE TABLE messages(
    id SERIAL PRIMARY KEY, 
    user_id INT REFERENCES users(id) NOT NULL, 

    content TEXT NOT NULL, 
     
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
    );


CREATE TABLE pm (
    id SERIAL PRIMARY KEY, 
    sender_id INT REFERENCES users(id) NOT NULL, 
recipient_id INT REFERENCES users(id) NOT NULL, 
    content TEXT NOT NULL, 
     
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
    );


